
import { useRef, useCallback } from 'react';

// A simple, pleasant chime sound embedded as a Base64 data URI
const COMPLETION_SOUND_URI = 'data:audio/mpeg;base64,-';

export const useSound = (soundEnabled: boolean) => {
  const audioRef = useRef<HTMLAudioElement | null>(null);

  if (typeof window !== 'undefined' && !audioRef.current) {
    audioRef.current = new Audio(COMPLETION_SOUND_URI);
    audioRef.current.preload = 'auto';
  }

  const playCompletionSound = useCallback(() => {
    if (soundEnabled && audioRef.current) {
      audioRef.current.currentTime = 0;
      audioRef.current.play().catch(error => {
        console.error("Audio play failed:", error);
      });
    }
  }, [soundEnabled]);

  return { playCompletionSound };
};
